mapper.select:
SELECT s.id,s.category_id,s.name,s.price,s.status,s.description,s.image,s.update_time,c.name AS categoryName FROM setmeal s left JOIN category c
    on s.category_id=c.id

    s.name=#{name}
    s.status=#{status}
    s.category_id=#{categoryId}

mapper.insert:
        insert into setmeal_dish(id, setmeal_id, dish_id, name, price, copies)
values (
        #{id},#{setmealId},#{dishId},#{name},#{price},#{copies}
       )
